  }


  const getDelay = () => { return Math.floor(Math.random() * (1100 - 1500) ) + 1500;}

  const respondNB = () => {
    setTimeout(function() { sendMessage(NB.name, NB.thinking) }, getDelay());  
  }

  const respondDNN = () => {
    setTimeout(function() { sendMessage(DNN.name, DNN.thinking) }, getDelay());  
  }

  const handleMessage = (event) => {
    event.preventDefault();
    // Render user message
    sendMessage(name, message)
    setMessage('');

    // Randomly alternate which bot responds to the message first
    if(Math.random() < 0.5){
      respondNB();
